/// <reference types="node" />
import { inspect } from 'util';
import { ICallbackServiceValidate } from '../utils/callback-service';
export interface IAPIRequestOptions {
    method: string;
    params: Record<string, any>;
}
export default class APIRequest {
    method: string;
    params: Record<string, any>;
    attempts: number;
    promise: Promise<any>;
    resolve: Function;
    reject: Function;
    captchaValidate?: ICallbackServiceValidate;
    /**
     * Constructor
     */
    constructor({ method, params }: IAPIRequestOptions);
    /**
     * Returns custom tag
     */
    get [Symbol.toStringTag](): string;
    /**
     * Adds attempt
     */
    addAttempt(): number;
    /**
     * Returns string to execute
     */
    toString(): string;
    /**
     * Custom inspect object
     */
    [inspect.custom](depth: number, options: Record<string, any>): string;
}
