import VK from '../vk';
export interface IResolvedResource {
    id: number;
    owner?: number;
    type: 'user' | 'group' | 'application' | 'albums' | 'album' | 'wall' | 'club' | 'photo' | 'video' | 'audio' | string;
}
export default class ResourceResolver {
    protected vk: VK;
    /**
     * Constructor
     */
    constructor(vk: VK);
    /**
     * Resolve resource
     */
    resolve(rawResource: string | number): Promise<IResolvedResource>;
    /**
     * Resolve number
     */
    protected resolveNumber(resource: number): Promise<IResolvedResource>;
    /**
     * Resolve resource mention
     */
    protected resolveMention(resource: string): Promise<IResolvedResource>;
    /**
     * Resolve resource url
     */
    protected resolveUrl(rawResourceUrl: string): Promise<IResolvedResource>;
    /**
     * Resolve screen name
     */
    protected resolveScreenName(resource: string): Promise<IResolvedResource>;
}
