import VK from '../../vk';
import Attachment from './attachment';
export interface IMarketAttachmentPayload {
    id: number;
    owner_id: number;
    access_key?: string;
    title?: string;
    description?: string;
    price?: {
        amount: number;
        currency: {
            id: number;
            name: string;
        };
    };
    category?: {
        id: number;
        name: string;
        section: {
            id: number;
            name: string;
        };
    };
    thumb_photo?: string;
    date?: number;
    availability?: 0 | 1 | 2;
    is_favorite?: number;
    photos?: object[];
    can_comment?: number;
    can_repost?: number;
    likes?: {
        user_likes: number;
        count: number;
    };
    url?: string;
    button_title?: string;
}
export default class MarketAttachment extends Attachment<IMarketAttachmentPayload> {
    /**
     * Constructor
     */
    constructor(payload: IMarketAttachmentPayload, vk?: VK);
    /**
     * Load attachment payload
     */
    loadAttachmentPayload(): Promise<void>;
    /**
     * Checks is bookmarked current user
     */
    get isFavorited(): boolean | null;
}
