/// <reference types="node" />
import { URL } from 'url';
import VK from '../../vk';
export default class PollingTransport {
    started: boolean;
    /**
     * 2 -  Attachments
     * 8 -  Extended events
     * 64 - Online user platform ID
     */
    mode: number;
    pollingHandler: Function;
    protected vk: VK;
    protected ts: string | number;
    protected pts: number;
    protected restarted: number;
    protected url: URL;
    constructor(vk: VK);
    start(): Promise<void>;
    /**
     * Stopping gets updates
     */
    stop(): Promise<void>;
    /**
     * Starts forever fetch updates  loop
     */
    protected startFetchLoop(): Promise<void>;
    /**
     * Gets updates
     */
    fetchUpdates(): Promise<void>;
    subscribe(handler: Function): void;
}
